import React, {Component} from 'react';

export default class MyCheckMulti extends Component{
    constructor(props){
        super(props);

        this.state = {
            fruit:['apple', 'melon'],
        };

        this.handleChangeMulti = this.handleChangeMulti.bind(this);
        this.show = this.show.bind(this);
    }

    handleChangeMulti(e){
        const fs = this.state.fruit;

        if(e.target.checked){
            fs.push(e.target.value);
        } else{
            fs.splice(fs.indexOf(e.target.value), 1);
        }

        this.setState({
            [e.target.name]: fs
        });
    }

    show(){
        console.log(`好きな果物：${this.state.fruit}`);
    }

    render(){
        return(
            <form>
                <fieldset>
                    <legend>好きな果物：</legend>
                    <label htmlFor="fruit_apple">リンゴ</label>
                    <input id="fruit_apple" name="fruit" type="checkbox" value="apple" checked={this.state.fruit.includes('apple')} /><br/>

                    <label htmlFor="fruit_orange">オレンジ</label>
                    <input id="fruit_orange" name="fruit" type="checkbox" value="orange" checked={this.state.fruit.includes('orange')} /><br/>

                    <label htmlFor="fruit_melon">メロン</label>
                    <input id="fruit_melon" name="fruit" type="checkbox" value="melon" checked={this.state.fruit.includes('melon')} /><br/>

                    <label htmlFor="fruit_grape">葡萄</label>
                    <input id="fruit_grape" name="fruit" type="checkbox" value="grape" checked={this.state.fruit.includes('grape')} /><br/>

                    <label htmlFor="fruit_strawberry">苺</label>
                    <input id="fruit_strawberry" name="fruit" type="checkbox" value="strawberry" checked={this.state.fruit.includes('strawberry')} /><br/>
                </fieldset>
                <button type="button" onClick={this.show}>送信</button>
            </form>
        );
    }
}
